"$:/plugins/Telumire/Animate/styles/scroll": {
    "created": "20230416203433617",
    "text": "\rules except dash list

:root{
--scroll-down:<$text text={{{ [{$:/temp/animate/scroll-direction}match[down]then[1]else[0]] }}}/>;
--scroll-transition-timing:0.2s;
}

<$list filter="[[$:/plugins/tiddlywiki/menubar/menu]has[title]]" variable="ignore">
/* Animate the menubar from the menubar plugin*/
.tc-menubar{
transition: transform var(--scroll-transition-timing);
transform: translateY(calc(var(--scroll-down) * -100%)) translateY(0px);
}

.tc-tiddler-title{
--top-nav-height:28px;
transition: top var(--scroll-transition-timing);
top:calc(var(--scroll-down) * var(--top-nav-height) * -1 + var(--top-nav-height));
}

</$list>",
