    "created": "20230415224833603",
    "text": ".tc-tiddler-controls svg{
	fill: <<colour tiddler-controls-foreground>>;
}

.tc-tiddler-controls .tc-checkbox:hover svg{
	fill: <<colour tiddler-controls-foreground-hover>>;
}

.tc-btn-invisible.tc-tiddler-controls.show *{
opacity:1;
}",
