"$:/plugins/Telumire/Commons/styles/checkbox": {
    "created": "20230415225146171",
    "text": ".tc-cbx-invisible{
	cursor:pointer;
}

.tc-cbx-invisible input{
	display:none;
}",
    "tags": "$:/tags/Stylesheet",
