},

"$:/plugins/Telumire/Commons/styles/table": {
    "created": "20230415224914406",
    "text": ".tc-max-width.tc-edit-max-width{
display:grid;
grid-template-columns: fit-content(30%) 1fr;
}

.tc-max-width.tc-edit-max-width :is(
.tc-edit-tags, select
){
flex:1;
}

.tc-max-width.tc-edit-max-width :not(td, td *){
display:contents;
}

.one-column,
.one-column.tc-max-width.tc-edit-max-width {
grid-template-columns: 1fr;
gap:.5ch;
}

.one-column  td{
padding:0;
display:flex;
flex-wrap: wrap;
gap:1ch;
}

.one-column  td .tc-editor-toolbar{
display:none;
}

.one-column :is(input,textarea,iframe){
flex:1;
}

.two-equal-column{
display:grid;
grid-template-columns: 1fr 1fr;
}

.two-equal-column :is(tr,thead,tbody){
display:contents;
}

.tc-max-width.tc-edit-max-width td+td,
.two-equal-column.flex-input td+td{
display:flex;
flex-wrap: wrap;
gap:.5ch;
}

.no-borders, .no-borders td{
border:none;
}",
    "tags": "$:/tags/Stylesheet",
    "title": "$:/plugins/Telumire/Commons/styles/table",
    "modified": "20230419062338560",
    "type": "",
    "list-after": ""
}}
}
