{"tiddlers": {
"$:/plugins/Telumire/Switcher/buttons/ThemeSwitcher": {
    "created": "20220529203036772",
    "text": "\whitespace trim
\define trigger-popup()
<$action-popup $state=<<qualify $:/state/>> $coords=<<tv-popup-coords>>/>
\end
\define set-palette-action(scheme)
<$action-setfield $tiddler="$:/palette" text={{$:/plugins/Telumire/Switcher/palettes##$scheme$}}/>
\end
\define switch-scheme()
<$macrocall $name="set-palette-action" scheme={{{ [{$:/palette}get[color-scheme]toggle[light],[dark]] }}}/>
\end
\define select-palettes(scheme) 
    <li title="$scheme$ theme">
        <svg viewBox="0 0 20 20">
            <circle cx="10" cy="10" r="8" stroke-width="2px" stroke="currentColor" fill={{{ [[$scheme$]match[dark]then[currentColor]else[none]] }}}/>
        </svg>
        <$select index="$scheme$" actions=<<set-palette-action $scheme$>> >
            <$list filter="[[$:/tags/Palette]tagging[]]:filter[get[color-scheme]match[$scheme$]]">
                <option value={{!!title}}><$view field="name"/></option> 
            </$list>
        </$select>
    </li>
\end
<$tiddler tiddler="$:/plugins/Telumire/Switcher/palettes">
    <$eventcatcher selector="button" $contextmenu=<<trigger-popup>> >
        <$button tooltip="Switch colours" aria-label="Switch colours" class={{{ colorSwitch [<tv-config-toolbar-class>] [<qualify $:/state/>!is[missing]then[tc-selected]]+[join[ ]]}}} actions=<<switch-scheme>>>
            <$list filter="[<tv-config-toolbar-icons>match[yes]]">
                {{$:/plugins/Telumire/Switcher/images/ThemeSwitcher}}
            </$list>
            <$list filter="[<tv-config-toolbar-text>match[yes]]">
                <span class="tc-btn-text">Switch colours</span>
            </$list>
        </$button>
    </$eventcatcher>
    <$reveal state=<<qualify $:/state/>> type="popup" position="bottom" class="tc-drop-down tc-popup-keep" tag="menu">
        <ul>
            <<select-palettes dark>>
            <<select-palettes light>>
        </ul>
    </$reveal>
</$tiddler>",
