"$:/plugins/Telumire/Switcher/settings": {
    "created": "20230416145110336",
    "text": "\import $:/plugins/Telumire/Switcher/buttons/ThemeSwitcher
\define palette(scheme)
<td style="vertical-align: top;">
<$list filter="[[$:/tags/Palette]tagging[]]:filter[get[color-scheme]match[$scheme$]]" variable="palette">
<div><$checkbox tiddler="$:/plugins/Telumire/Switcher/palettes" index="$scheme$" checked=<<palette>> actions=<<set-palette-action $scheme$>>> <$view field="name" tiddler=<<palette>> /></$checkbox></div>
</$list>
</td>
\end

\define th(scheme)
<th>
<svg viewBox="0 0 20 20" width="1.5ch">
<circle cx="10" cy="10" r="8" stroke-width="2px" stroke="currentColor" fill={{{ [[$scheme$]match[dark]then[currentColor]else[none]] }}}/>
</svg> $scheme$
</th>
\end

\define time-input(field)
<$edit-text type="time" field="$field$"/>
\end

\define mousedown-action()
<$action-setfield from=<<dom-data-item-hour>>/>
\end

\define mouseup-action()
<$action-setfield to=<<dom-data-item-hour>>>
\end

<table style="width:100%;table-layout:fixed;">
<thead>
<<th light>><<th dark>>
</thead>
<<palette light>><<palette dark>>
</table>

<$let
from={{{ [{$:/plugins/Telumire/Switcher/settings!!from}split[:]first[]] }}}
to={{{ [{$:/plugins/Telumire/Switcher/settings!!to}split[:]first[]] }}}
time=<<now "0hh">>
same-day="[<from>compare:number:lt<to>]"
accross="[range<from>,[24]] [range[1],<to>]"
within="[range<from>,<to>]"
>


<$checkbox field="auto-dark-on-start" checked="true"> Automatically switch to darkmode on startup</$checkbox>:  @@display:flex;gap:.5ch;justify-content:center;.mobile-only <<time-input from>> - <<time-input to>>@@

<$list filter="[{$:/plugins/Telumire/Switcher/settings!!auto-dark-on-start}match[true]]">
<$macrocall $name="set-palette-action" scheme={{{
[<time>compare:number:gt<from>compare:number:lt<to>filter<same-day>]
[<time>compare:number:lt<to>!filter<same-day>]
[<time>compare:number:gt<from>!filter<same-day>]
+[then[dark]else[light]]
}}}/>
</$list>


<$eventcatcher selector="li" $mouseup=<<mouseup-action>> $mousedown=<<mousedown-action>> >
<div class="time-picker desktop-only" >
<ul>
<$list filter="[range[1],[12]pad[2]]" variable="hour">
<li data-item-hour=<<hour>> class={{{ 
[subfilter<within>] :filter[<from>compare:number:lt<to>]:else[subfilter<accross>] +[pad[2]]
:filter[<hour>match{!!title}]+[limit[1]then[selected]]
}}}><<hour>></li>
</$list>
</ul>
<ul>
<$list filter="[range[13],[24]]" variable="hour">
<li data-item-hour=<<hour>> class={{{ 
[subfilter<within>] :filter[<from>compare:number:lt<to>]:else[subfilter<accross>] +[pad[2]]
:filter[<hour>match{!!title}]+[limit[1]then[selected]]
}}}><<hour>></li>
</$list>
</ul>
</div>
</$eventcatcher>


<style>
.time-picker ul{
display:grid;
grid-template-columns: repeat(12, 1fr);
justify-items: center;
list-style:none;
padding:0;
}
.time-picker li{width:100%;text-align:center;cursor:pointer;}
.time-picker li.selected{background:black;color:white;}

@media (pointer: coarse) {
.desktop-only {pointer-events:none!important;}
}

@media (any-hover: hover) {
.mobile-only{ display:none!important;}
}
</style>",
