"$:/plugins/Telumire/Switcher/styles/ThemeSwitcher": {
    "created": "20220618125331127",
    "text": "menu.tc-drop-down.tc-popup-keep :is(ul,li){
    all:unset;
    display:grid;
    color:<<color foreground>>;
}

menu.tc-drop-down.tc-popup-keep{
    min-width:unset;
    margin:0;
    padding:0;
}

menu.tc-drop-down.tc-popup-keep li{
    position:relative;
    padding:.5ch;
    padding-left: 0;
}

menu.tc-drop-down.tc-popup-keep li svg{
    position: absolute;
    pointer-events: none;
    height: 100%;
    left: 5pt;
}

menu.tc-drop-down.tc-popup-keep li+li:after{
    content:"";
    position:absolute;
    inset:0;
    opacity:.3;
    pointer-events:none;
    border-top: thin ridge currentColor;
}

menu.tc-drop-down.tc-popup-keep li select{
    all:unset;
    cursor:pointer;
    padding-left: 20pt;
}

menu.tc-drop-down.tc-popup-keep,
menu.tc-drop-down.tc-popup-keep li :is(select,option){
    background-color:<<color tiddler-background>>;
    color:<<color tiddler-foreground>>;
}",
    "title": "$:/plugins/Telumire/Switcher/styles/ThemeSwitcher",
